mapper.insert:
insert into comment (com_id, com_post_id, com_man, 
  com_commen, com_agree_count, com_context
  )
values (#{comId,jdbcType=INTEGER}, #{comPostId,jdbcType=INTEGER}, #{comMan,jdbcType=VARCHAR}, 
  #{comCommen,jdbcType=TIMESTAMP}, #{comAgreeCount,jdbcType=INTEGER}, #{comContext,jdbcType=LONGVARCHAR}
  )

mapper.select:
select com_id, com_post_id, com_man, com_commen, com_agree_count, com_context
from comment
where com_id = #{comId,jdbcType=INTEGER}
