mapper.insert:
insert into country (courtry_id, courtry_name, courtry_pid
  )
values (#{courtryId,jdbcType=INTEGER}, #{courtryName,jdbcType=VARCHAR}, #{courtryPid,jdbcType=INTEGER}
  )

mapper.update:
update country
set courtry_name = #{courtryName,jdbcType=VARCHAR},
  courtry_pid = #{courtryPid,jdbcType=INTEGER}
where courtry_id = #{courtryId,jdbcType=INTEGER}

mapper.select:
select courtry_id, courtry_name, courtry_pid
from country
where courtry_id = #{courtryId,jdbcType=INTEGER}
