mapper.update:
update post
set post_man = #{postMan,jdbcType=VARCHAR},
  post_time = #{postTime,jdbcType=TIMESTAMP},
  post_comment_count = #{postCommentCount,jdbcType=INTEGER},
  post_context = #{postContext,jdbcType=LONGVARCHAR}
where post_id = #{postId,jdbcType=INTEGER}

mapper.select:
select post_id, post_man, post_time, post_comment_count, post_context
from post
where post_id = #{postId,jdbcType=INTEGER}
